from flask import Flaskfrom flask import requestfrom flask import sessionfrom flask import make_responsefrom flask import redirectfrom flask import abort# from flask.ext.bootstrap import Bootstrapfrom flask_bootstrap import Bootstrapapp = Flask(__name__)bootstrap = Bootstrap(app)@app.route('/hello')def hello_world():    return '<h1>Hello World!</h1>'@app.route('/user/<username>')def index(username):    return '<h2>hello,%s!</h2>' % username@app.route('/')def home():    user_agent = request.headers.get('User-Agent')    print(user_agent)    return '<h3>Your browser is %s</p>' % user_agent@app.route('/abc')def abc():    response = make_response('<h1>This document carries a cookie!!!</h1>')    response.set_cookie('answer','twy')    return response@app.route('/baidu')def baidu():    return redirect('https://www.baidu.com')@app.route('/user2/<id>')def get_user(id):    user = load_user(id)    if not user:        abort(404)    return '<h1>Hello, %s</h1>' % user.nameclass load_user:    def __init__(self,id):        self.name = 'twy&dw!' + idif __name__ == '__main__':    app.run(debug=True)