from flask import Flask,render_templatefrom flask_script import Managerfrom flask_bootstrap import Bootstrapfrom flask_moment import Momentfrom datetime import datetimeapp = Flask(__name__,template_folder="../templates")bootstrap = Bootstrap(app)moment = Moment(app)# manager = Manager(app)@app.route('/manager')def mymanager():    return '<h1>This is manager!</h1>'@app.route('/user/<name>')def Home(name):    return render_template('bootstrap.html',name=name)@app.route('/')def home():    return render_template('bootstrap.html')@app.route('/time')def time():    return render_template('bootstrap.html',                            current_time=datetime.utcnow())if __name__ == '__main__':    app.run()